import React, { Component } from 'react';

import MVAnimate from './MVAnimate';

class MVLogic extends Component {

	constructor(props){
		super(props);
		this.state = {
			style1: 'hidden',
			style2: 'hidden',
			style3: 'hidden',
			animation: 'hidden'
		}
	}

	componentDidMount() {
    window.onscroll = () => this.handleAnimation();
	}

	handleAnimation = () => {
		if(document.documentElement.scrollTop > 50){
			this.setState({
				style1: 'style1',
				animation: 'animation'
			});
		}

		if(document.documentElement.scrollTop > 100){
			this.setState({
				style2: 'style2',
				animation: 'animation'
			});
		}

		if(document.documentElement.scrollTop > 150){
			this.setState({
				style3: 'style3',
				animation: 'animation'
			});
		}
	};

	render(){
		return(
			<MVAnimate classStyle={this.state.style1} classAnimation={this.state.animation} text='ï¿½ QUIENES SON'/>
		);
	}
}
export default MVLogic;
